"use client";

import {
  PricingDialog,
  PricingDialogButton,
  PricingDialogFooter,
  PricingDialogTitle,
  Information,
} from "@/components/pricing/pricing-dialog";
import { CheckFeatureFormattedPreview } from "../../package/dist/sdk/check";
import { useAutumn } from "autumn-js/next";

export interface PaywallDialogProps {
  open: boolean;
  setOpen: (open: boolean) => void;
  preview: CheckFeatureFormattedPreview;
}

export default function PaywallDialog(params?: PaywallDialogProps) {
  const { attach } = useAutumn();
  if (!params || !params.preview) {
    return <></>;
  }

  const { open, setOpen } = params;
  const { title, message, feature_id, feature_name, next_tier } =
    params.preview;

  console.log("Feature ID", feature_id);
  console.log("Feature Name", feature_name);

  return (
    <PricingDialog open={open} setOpen={setOpen}>
      <PricingDialogTitle>{title || "Feature Unavailable"}</PricingDialogTitle>
      <Information className="mb-2">{message}</Information>
      <PricingDialogFooter>
        <PricingDialogButton
          size="sm"
          className="font-medium shadow transition min-w-20"
          onClick={async () => {
            if (next_tier) {
              await attach({
                productId: next_tier.id,
              });
            }
          }}
        >
          {next_tier ? `Upgrade to ${next_tier.name}` : "Contact Us"}
        </PricingDialogButton>
      </PricingDialogFooter>
    </PricingDialog>
  );
}
